mapper.select:
select


from app_permanet_calendar



		yearMonth = #{yearMonth,jdbcType=VARCHAR}


		and	dayCount = #{dayCount,jdbcType=VARCHAR}


		and festivalInfo = #{festivalinfo,jdbcType=VARCHAR}


		and lumarInfo1 = #{lumarinfo1,jdbcType=VARCHAR}


		and lumarInfo2 = #{lumarinfo2,jdbcType=VARCHAR}


		and dayViewYiDescContent = #{dayviewyidesccontent,jdbcType=VARCHAR}


		and dayViewJiDescContent = #{dayviewjidesccontent,jdbcType=VARCHAR}


		and pzbj = #{pzbj,jdbcType=VARCHAR}


		and jsyq = #{jsyq,jdbcType=VARCHAR}


		and xsyj = #{xsyj,jdbcType=VARCHAR}


		and cs = #{cs,jdbcType=VARCHAR}


		and wx = #{wx,jdbcType=VARCHAR}


		and create_date = #{createDate,jdbcType=TIMESTAMP}


		and comments = #{comment,jdbcType=VARCHAR}
